CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_GetSchedulerBooking (IN BuildingID char(36), IN UserID char(36), IN RoomID char(36), IN ListEquipment text, IN CapacityMin int, IN CapacityMax int, IN PageSize int, IN PageIndex int, OUT TotalRecord int, OUT TotalPage int)
BEGIN
  DECLARE $offset int DEFAULT 0;
  SET $offset = PageSize * (PageIndex - 1);
  CREATE TEMPORARY TABLE IF NOT EXISTS SchedulerBooking (
    BookingRoomID char(36),
    RoomID char(36),
    RoomName varchar(255),
    FullName varchar(255),
    Quantity int,
    RoomStatus int,
    EndDate datetime,
    StartDate datetime,
    AvartarColor text,
    Subject varchar(255),
    Description text,
    TimeSlots text,
    StatusBooking text
  );
  IF ListEquipment IS NOT NULL THEN
    INSERT INTO SchedulerBooking
      SELECT
      DISTINCT
        b.BookingRoomID,
        r.RoomID,
        r.RoomName,
        u.FullName,
        b.Quantity,
        r.RoomStatus,
        b.EndDate,
        b.StartDate,
        u.AvartarColor,
        b.Subject,
        b.Description,
        b.TimeSlots,
        b.StatusBooking
      FROM bookingroom b
        LEFT JOIN (SELECT
            room.*
          FROM room
          WHERE NOT EXISTS (SELECT
              *
            FROM JSON_TABLE (ListEquipment, "$[*]" COLUMNS (equipment_id varchar(255) PATH "$")) AS e
            WHERE NOT EXISTS (SELECT
                *
              FROM roomequipment
              WHERE roomequipment.RoomID = room.RoomID
              AND roomequipment.EquipmentID = e.equipment_id)) LIMIT $offset, PageSize) AS r
          ON b.RoomID = r.RoomID
        JOIN user u
          ON b.UserID = u.UserID
        JOIN building b1
          ON r.BuildingID = b1.BuildingID
      WHERE ((BuildingID IS NULL
      OR BuildingID = b1.BuildingID)
      AND (RoomID IS NULL
      OR RoomID = b.RoomID)
      AND (UserID IS NULL
      OR UserID = b.UserID)
      AND (r.Capacity BETWEEN IFNULL(CapacityMin, 0) AND IFNULL(CapacityMax, 5000)))
      AND b.StatusBooking != 4;
  ELSE

    INSERT INTO SchedulerBooking
      SELECT
      DISTINCT
        b.BookingRoomID,
        r.RoomID,
        r.RoomName,
        u.FullName,
        b.Quantity,
        r.RoomStatus,
        b.EndDate,
        b.StartDate,
        u.AvartarColor,
        b.Subject,
        b.Description,
        b.TimeSlots,
        b.StatusBooking
      FROM bookingroom b
        JOIN room r
          ON b.RoomID = r.RoomID
        JOIN user u
          ON b.UserID = u.UserID
        JOIN building b1
          ON r.BuildingID = b1.BuildingID
      WHERE ((BuildingID IS NULL
      OR BuildingID = b1.BuildingID)
      AND (RoomID IS NULL
      OR RoomID = b.RoomID)
      AND (UserID IS NULL
      OR UserID = b.UserID)
      AND (r.Capacity BETWEEN IFNULL(CapacityMin, 0) AND IFNULL(CapacityMax, 5000)))
      AND b.StatusBooking != 4;
  END IF;


  CREATE TEMPORARY TABLE IF NOT EXISTS tmpRoomResult (
    RoomID char(36),
    RoomCode varchar(20),
    RoomName varchar(255),
    Capacity int,
    ListEquipmentName varchar(5000),
    AvartarAdmin text,
    AdminName varchar(255),
    AdminEmail varchar(50)
  );
  INSERT INTO tmpRoomResult
    SELECT
      r.RoomID,
      r.RoomCode,
      r.RoomName,
      r.Capacity,
      r.ListEquipmentName,
      r.AvartarAdmin,
      r.AdminName,
      r.AdminEmail
    FROM (SELECT
        room.*
      FROM room
      WHERE NOT EXISTS (SELECT
          *
        FROM JSON_TABLE (ListEquipment, "$[*]" COLUMNS (equipment_id varchar(255) PATH "$")) AS e
        WHERE NOT EXISTS (SELECT
            *
          FROM roomequipment
          WHERE roomequipment.RoomID = room.RoomID
          AND roomequipment.EquipmentID = e.equipment_id))) AS r
    WHERE (BuildingID IS NULL
    OR r.BuildingID = BuildingID)
    AND (RoomID IS NULL
    OR r.RoomID = RoomID)
    AND (r.Capacity BETWEEN IFNULL(CapacityMin, 0) AND IFNULL(CapacityMax, 5000))
    ORDER BY r.RoomName;

  SELECT
  DISTINCT
    *
  FROM SchedulerBooking a
    INNER JOIN (SELECT
        *
      FROM tmpRoomResult
      LIMIT $offset, PageSize) r
      ON r.RoomID = a.RoomID;
  SELECT
    *
  FROM tmpRoomResult AS Room
  LIMIT $offset, PageSize
  ;
  SET TotalRecord = (SELECT
      COUNT(*)
    FROM tmpRoomResult);
  SET TotalPage = CEILING(TotalRecord / IFNULL(PageSize, TotalRecord));
  DROP TABLE IF EXISTS tmpRoomResult;
