CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_GetRoomPaging ($Date text, $RoomCode varchar(20), $BuildingName varchar(255), $Type int)
BEGIN
  IF $Type = 1 THEN
  BEGIN
    SELECT
      c.UserID,
      a.RoomCode,
      a.BuildingName,
      a.RoomName,
      a.Capacity,
      a.RoomStatus,
      a.TimeSlotName,
      a.StartTime,
      a.EndTime,
      a.Date,
      'Còn trống' isOpen
    FROM (SELECT
