      b1.BuildingName,
      r.RoomName,
      r.Capacity,
      r.RoomStatus,
      t.TimeSlotName,
      t.StartTime,
      t.EndTime,
      b.DateBooking AS Date,
      'Đang sử dụng' isOpen
    FROM bookingroom b
      INNER JOIN room r
        ON b.RoomID = r.RoomID
      INNER JOIN timeslot t
        ON b.TimeSlotID = t.TimeSlotID
      INNER JOIN building b1
        ON r.BuildingID = b1.BuildingID
    WHERE (r.RoomCode = $RoomCode
    OR $RoomCode = 'All')
    AND (b1.BuildingName = $BuildingName
    OR $BuildingName = 'All')
    ORDER BY Date, r.RoomCode, b1.BuildingName;
  END;
  ELSEIF $Type = 3 THEN
  BEGIN
    SELECT
      c.UserID,
      a.RoomCode,
      a.BuildingName,
      a.RoomName,
      a.Capacity,
      a.RoomStatus,
      a.TimeSlotName,
      a.StartTime,
      a.EndTime,
      a.Date,
      'Còn trống' isOpen
    FROM (SELECT
        r.RoomID,
        r.RoomCode,
        r.RoomTypeID,
        r.RoomName,
        r.Capacity,
        r.RoomStatus,
        t.TimeSlotID,
        t.TimeSlotName,
        t.StartTime,
        t.EndTime,
        b1.BuildingID,
        b1.BuildingCode,
        b1.BuildingName,
        b.Date
      FROM room r
        CROSS JOIN timeslot t
        JOIN building b1
          ON r.BuildingID = b1.BuildingID
        CROSS JOIN (SELECT
            Date
          FROM JSON_TABLE ($Date, '$[*]' COLUMNS (Date datetime PATH '$')) AS d) b) a
      LEFT JOIN bookingroom c
        ON a.RoomID = c.RoomID
        AND a.TimeSlotID = c.TimeSlotID
        AND DATEDIFF(c.DateBooking, a.Date) = 0
    WHERE (a.RoomCode = $RoomCode
    OR $RoomCode = 'All')
    AND (a.BuildingName = $BuildingName
    OR $BuildingName = 'All')

    UNION
    SELECT DISTINCT
      b.UserID,
      r.RoomCode,
      b1.BuildingName,
      r.RoomName,
      r.Capacity,
      r.RoomStatus,
      t.TimeSlotName,
      t.StartTime,
      t.EndTime,
      b.DateBooking AS Date,
      'Đang sử dụng' isOpen
    FROM bookingroom b
      INNER JOIN room r
        ON b.RoomID = r.RoomID
      INNER JOIN timeslot t
        ON b.TimeSlotID = t.TimeSlotID
      INNER JOIN building b1
        ON r.BuildingID = b1.BuildingID
    WHERE (r.RoomCode = $RoomCode
    OR $RoomCode = 'All')
    AND (b1.BuildingName = $BuildingName
    OR $BuildingName = 'All');
  END;
  END IF;


END
