--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_GetSchedulerRoomPaging (IN RoomFilter varchar(255), OUT TotalRecord int, OUT TotalPage int, IN PageSize int, IN PageIndex int, ListDate text, RoomID char(36), BuildingID char(36), TimeSlotID char(36), Type int)
BEGIN
  CREATE TEMPORARY TABLE IF NOT EXISTS RoomFilters (
    UserID char(36),
    RoomCode varchar(20),
