        a.RoomName,
        a.Capacity,
        a.RoomStatus,
        a.TimeSlotName,
        a.StartTime,
        a.EndTime,
        a.Date,
        'Còn trống' isOpen
      FROM (SELECT
          r.RoomID,
          r.RoomCode,
          r.RoomTypeID,
          r.RoomName,
          r.Capacity,
          r.RoomStatus,
          t.TimeSlotID,
          t.TimeSlotName,
          t.StartTime,
          t.EndTime,
          b1.BuildingID,
          b1.BuildingCode,
          b1.BuildingName,
          b.Date
        FROM room r
          CROSS JOIN timeslot t
          JOIN building b1
            ON r.BuildingID = b1.BuildingID
          CROSS JOIN (SELECT
              Date
            FROM JSON_TABLE (ListDate, '$[*]' COLUMNS (Date datetime PATH '$')) AS d) b) a
        LEFT JOIN bookingroom c
          ON a.RoomID = c.RoomID
        JOIN timebooking t
          ON c.BookingRoomID = t.BookingRoomID
          AND a.TimeSlotID = t.TimeSlotID
          AND DATEDIFF(c.DateBooking, a.Date) = 0
      WHERE c.UserID IS NULL
      AND (a.RoomID = RoomID
      OR RoomID IS NULL)
      AND (a.BuildingID = BuildingID
      OR BuildingID IS NULL)
      AND (t.TimeSlotID = TimeSlotID
      OR TimeSlotID IS NULL)
      ORDER BY a.Date, a.RoomCode, a.BuildingName, a.TimeSlotID;
  END;
  ELSEIF Type = 2 THEN
  BEGIN
    INSERT INTO RoomFilters
      SELECT DISTINCT
        b.UserID,
        r.RoomCode,
        b1.BuildingName,
        r.RoomName,
        r.Capacity,
        r.RoomStatus,
        t.TimeSlotName,
        t.StartTime,
        t.EndTime,
        b.DateBooking AS Date,
        'Đang sử dụng' isOpen
      FROM bookingroom b
        JOIN timebooking t1
          ON b.BookingRoomID = t1.BookingRoomID
        INNER JOIN room r
          ON b.RoomID = r.RoomID
        INNER JOIN timeslot t
          ON t1.TimeSlotID = t.TimeSlotID
        INNER JOIN building b1
          ON r.BuildingID = b1.BuildingID
      WHERE (r.RoomID = RoomID
      OR RoomID IS NULL)
      AND (b1.BuildingID = BuildingID
      OR BuildingID IS NULL)
      AND (t1.TimeSlotID = TimeSlotID
      OR TimeSlotID IS NULL)
      ORDER BY Date, r.RoomCode, b1.BuildingName;
  END;
  ELSEIF Type = 3 THEN
  BEGIN
    INSERT INTO RoomFilters
      SELECT
        c.UserID,
        a.RoomCode,
        a.BuildingName,
        a.RoomName,
        a.Capacity,
        a.RoomStatus,
        a.TimeSlotName,
        a.StartTime,
        a.EndTime,
        a.Date,
        'Còn trống' isOpen
      FROM (SELECT
          r.RoomID,
          r.RoomCode,
          r.RoomTypeID,
          r.RoomName,
          r.Capacity,
          r.RoomStatus,
          t.TimeSlotID,
          t.TimeSlotName,
          t.StartTime,
          t.EndTime,
          b1.BuildingID,
          b1.BuildingCode,
          b1.BuildingName,
          b.Date
        FROM room r
          CROSS JOIN timeslot t
          JOIN building b1
            ON r.BuildingID = b1.BuildingID
          CROSS JOIN (SELECT
              Date
            FROM JSON_TABLE (ListDate, '$[*]' COLUMNS (Date datetime PATH '$')) AS d) b) a
        LEFT JOIN bookingroom c
          ON a.RoomID = c.RoomID
        JOIN timebooking t1
          ON c.BookingRoomID = t.BookingRoomID
          AND t.TimeSlotID = t1.TimeSlotID
          AND DATEDIFF(c.DateBooking, a.Date) = 0
      WHERE (a.RoomID = RoomID
      OR RoomID IS NULL)
      AND (a.BuildingID = BuildingID
      OR BuildingID IS NULL)
      AND (t1.TimeSlotID = TimeSlotID
      OR TimeSlotID IS NULL)
      UNION
      SELECT DISTINCT
        b.UserID,
        r.RoomCode,
        b1.BuildingName,
        r.RoomName,
        r.Capacity,
        r.RoomStatus,
        t.TimeSlotName,
        t.StartTime,
        t.EndTime,
        b.DateBooking AS Date,
        'Đang sử dụng' isOpen
      FROM bookingroom b
        JOIN timebooking t1
          ON b.BookingRoomID = t1.BookingRoomID
        INNER JOIN room r
          ON b.RoomID = r.RoomID
        INNER JOIN timeslot t
          ON t1.TimeSlotID = t.TimeSlotID
        INNER JOIN building b1
          ON r.BuildingID = b1.BuildingID
      WHERE (r.RoomID = RoomID
      OR RoomID IS NULL)
      AND (b1.BuildingID = BuildingID
      OR BuildingID IS NULL)
      AND (t1.TimeSlotID = TimeSlotID
      OR TimeSlotID IS NULL);
  END;

  END IF;

  SET TotalRecord = (SELECT
      COUNT(*)
    FROM RoomFilters);
  SET TotalPage = CEILING(TotalRecord / IFNULL(PageSize, TotalRecord));
  SET @offset = PageSize * (PageIndex - 1);
  SET @query = CONCAT('Select * from RoomFilters u ');
  IF PageSize IS NOT NULL THEN
    SET @query = CONCAT(@query, ' LIMIT ', @offset, ',', IFNULL(PageSize, TotalRecord));
  END IF;
  PREPARE queryTemp FROM @query;
  EXECUTE queryTemp;
  DEALLOCATE PREPARE queryTemp;

  DROP TEMPORARY TABLE IF EXISTS RoomFilters;

END
$$

