--
-- Create procedure `Proc_GetPagingHistory`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_GetPagingHistory (IN KeyWord varchar(255), OUT TotalRecord int, OUT TotalPage int, IN PageSize int, IN PageIndex int, IN UserID char(36), IN CurrentDate datetime, IN TimeSlotID char(36))
BEGIN
  -- Option = 1 - người dùng
  -- Option = 2 - admin
  -- Option = 3 - phụ trách phòng
  CREATE TEMPORARY TABLE IF NOT EXISTS RequestBookingFilters (
    BookingRoomID char(36),
    RoomCode varchar(20),
    UserCode varchar(20),
    RoomName varchar(255),
    FullName varchar(100),
    Description varchar(5000),

    BuildingName varchar(255),
    DateRequest datetime,
    StartDate datetime,
    StatusBooking int,
    Subject varchar(255),
    CreatedDate datetime,
    AvartarColor text,
    RefusalReason text,
    TimeSlotName text
  );

  INSERT INTO RequestBookingFilters
    SELECT
      b.BookingRoomID,
      r.RoomCode,
      u.UserCode,
      r.RoomName,
      u.FullName,
      b.Description,
      b1.BuildingName,
      b.DateRequest,
      b.StartDate,
      b.StatusBooking,
      b.Subject,
      b.CreatedDate,
      u.AvartarColor,
      b.RefusalReason,
      GROUP_CONCAT(CONCAT('Ca ', t.TimeSlotName) ORDER BY t.TimeSlotName SEPARATOR '+') AS TimeSlotName
    FROM bookingroom b
      JOIN user u
        ON b.UserID = u.UserID
      JOIN room r
        ON b.RoomID = r.RoomID
      JOIN building b1
        ON r.BuildingID = b1.BuildingID
      JOIN timebooking t1
        ON b.BookingRoomID = t1.BookingRoomID
      JOIN timeslot t
        ON t1.TimeSlotID = t.TimeSlotID
    WHERE b.UserID = UserID
    AND b.StatusBooking != 1
    AND (KeyWord IS NULL
    || KeyWord = ''
    )
    AND (FIND_IN_SET(TimeSlotID, b.TimeSlots) > 0
    OR TimeSlotID IS NULL)
    AND (DATEDIFF(CurrentDate, b.StartDate) = 0
    OR CurrentDate IS NULL)
    GROUP BY b.BookingRoomID,
             r.RoomCode,
             u.UserCode,
             r.RoomName,
             u.FullName,
             b.Description,
             b1.BuildingName,
             b.DateRequest,
             b.StartDate,
             b.StatusBooking,
             b.Subject,
             b.CreatedDate,
             u.AvartarColor,
             b.RefusalReason
    ORDER BY b.StartDate;


  SET TotalRecord = (SELECT
      COUNT(*)
    FROM RequestBookingFilters);
  SET TotalPage = CEILING(TotalRecord / IFNULL(PageSize, TotalRecord));
  SET @offset = PageSize * (PageIndex - 1);
  SET @query = CONCAT('Select * from RequestBookingFilters u Order by u.CreatedDate');
  IF PageSize IS NOT NULL THEN
    SET @query = CONCAT(@query, ' LIMIT ', @offset, ',', IFNULL(PageSize, TotalRecord));
  END IF;
  PREPARE queryTemp FROM @query;
  EXECUTE queryTemp;
  DEALLOCATE PREPARE queryTemp;

  DROP TEMPORARY TABLE IF EXISTS RequestBookingFilters;
END
$$

