CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_GetNewCode (OUT UserNewCode varchar(255))
BEGIN
  SELECT
    @maxCode := MAX(CAST(SUBSTRING(u.UserCode, LENGTH('GV-') + 1) AS SIGNED))
  FROM user u
  WHERE (
  LOCATE('GV-', u.UserCode) = 1
  AND SUBSTRING(u.UserCode, LENGTH('GV-') + 1) REGEXP '^[0-9]+$');


  IF @maxCode >= 9999 THEN
    SET UserNewCode = CONCAT('GV-', @maxCode + 1);
  ELSE
    SET UserNewCode = CONCAT('GV-', LPAD(IFNULL(@maxCode, 0) + 1, 4, 0));
  END IF;
END
